-- Courses table schema
-- Run this SQL to create the necessary tables for the courses feature

CREATE TABLE IF NOT EXISTS Courses (
    id INT AUTO_INCREMENT PRIMARY KEY,
    code VARCHAR(20) NOT NULL,
    name VARCHAR(255) NOT NULL,
    section VARCHAR(20) DEFAULT '01',
    professor VARCHAR(255) NOT NULL,
    semester VARCHAR(50) NOT NULL,
    description TEXT,
    syllabusFile VARCHAR(500),
    category VARCHAR(50) DEFAULT 'core',
    color VARCHAR(50) DEFAULT 'bg-blue-200',
    crn VARCHAR(20),
    credits INT DEFAULT 3,
    days VARCHAR(50),
    time VARCHAR(100),
    location VARCHAR(100),
    slug VARCHAR(100) UNIQUE,
    prerequisites TEXT,
    professors JSON,
    syllabi JSON,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    INDEX idx_category (category),
    INDEX idx_code (code),
    INDEX idx_slug (slug)
);

CREATE TABLE IF NOT EXISTS CourseResources (
    id INT AUTO_INCREMENT PRIMARY KEY,
    course_id INT NOT NULL,
    name VARCHAR(255) NOT NULL,
    url VARCHAR(500) NOT NULL,
    description TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (course_id) REFERENCES Courses(id) ON DELETE CASCADE,
    INDEX idx_course_id (course_id)
);

